import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8080";

const api = axios.create({
  baseURL: API_URL,
  timeout: 10000, // 10 second timeout
});

// Intercept request to add JWT token
api.interceptors.request.use(config => {
  console.log("API Request:", config.method?.toUpperCase(), config.url, config.data);
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Intercept responses to handle errors
api.interceptors.response.use(
  response => {
    console.log("API Response:", response.status, response.config.url, response.data);
    return response;
  },
  error => {
    console.error('API Error:', error);
    console.error('API Error Config:', error.config);
    console.error('API Error Response:', error.response);
    if (error.code === 'ECONNABORTED') {
      console.error('Request timeout - server might be down');
    }
    return Promise.reject(error);
  }
);

// Helper functions for common API calls
export const authAPI = {
  login: (credentials) => api.post("/api/auth/login", credentials),
  signup: (userData) => api.post("/api/auth/signup", userData),
  register: (userData) => api.post("/api/auth/register", userData)
};

export const tasksAPI = {
  getAll: () => api.get("/api/tasks"),
  getToday: () => api.get("/api/tasks/today"),
  getUpcoming: () => api.get("/api/tasks/upcoming"),
  create: (task) => api.post("/api/tasks", task),
  update: (id, task) => api.patch(`/api/tasks/${id}`, task),
  delete: (id) => api.delete(`/api/tasks/${id}`)
};

export const listsAPI = {
  getAll: () => api.get("/api/lists"),
  getTasks: (listId) => api.get(`/api/lists/${listId}/tasks`),
  create: (list) => api.post("/api/lists", list),
  delete: (id) => api.delete(`/api/lists/${id}`)
};

export default api;
