import React, { useState, useEffect } from "react";
import api from "../services/api";

function ListsView() {
  const [lists, setLists] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [newListName, setNewListName] = useState("");
  const [newTask, setNewTask] = useState("");
  const [selectedList, setSelectedList] = useState(null);

  const fetchLists = async () => {
    try {
      setLoading(true);
      setError(null);
      const res = await api.get("/lists");
      setLists(res.data);
      if (res.data.length > 0 && !selectedList) {
        setSelectedList(res.data[0].id);
      }
    } catch (err) {
      console.error("Error fetching lists:", err);
      setError("Failed to load lists");
    } finally {
      setLoading(false);
    }
  };

  const fetchTasksForList = async (listId) => {
    if (!listId) return;
    try {
      const res = await api.get(`/lists/${listId}/tasks`);
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks for list:", err);
      setError("Failed to load tasks for this list");
    }
  };

  useEffect(() => {
    fetchLists();
  }, []);

  useEffect(() => {
    if (selectedList) {
      fetchTasksForList(selectedList);
    }
  }, [selectedList]);

  const createList = async (e) => {
    e.preventDefault();
    if (!newListName.trim()) return;

    try {
      const res = await api.post("/lists", { name: newListName });
      setNewListName("");
      fetchLists();
      setSelectedList(res.data.id);
    } catch (err) {
      console.error("Error creating list:", err);
      setError("Failed to create list");
    }
  };

  const addTaskToList = async (e) => {
    e.preventDefault();
    if (!newTask.trim() || !selectedList) return;

    try {
      await api.post("/tasks", {
        title: newTask,
        listId: selectedList,
        priority: "medium"
      });
      setNewTask("");
      fetchTasksForList(selectedList);
    } catch (err) {
      console.error("Error adding task:", err);
      setError("Failed to add task");
    }
  };

  const toggleComplete = async (taskId, completed) => {
    try {
      await api.patch(`/tasks/${taskId}`, { completed: !completed });
      fetchTasksForList(selectedList);
    } catch (err) {
      console.error("Error updating task:", err);
      setError("Failed to update task");
    }
  };

  const deleteTask = async (taskId) => {
    try {
      await api.delete(`/tasks/${taskId}`);
      fetchTasksForList(selectedList);
    } catch (err) {
      console.error("Error deleting task:", err);
      setError("Failed to delete task");
    }
  };

  const deleteList = async (listId) => {
    try {
      await api.delete(`/lists/${listId}`);
      fetchLists();
      if (selectedList === listId) {
        setSelectedList(null);
        setTasks([]);
      }
    } catch (err) {
      console.error("Error deleting list:", err);
      setError("Failed to delete list");
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case "high": return "text-red-500";
      case "medium": return "text-orange-500";
      case "low": return "text-blue-500";
      default: return "text-gray-500";
    }
  };

  const getPriorityText = (priority) => {
    return priority.charAt(0).toUpperCase() + priority.slice(1);
  };

  const selectedListData = lists.find(list => list.id === selectedList);

  return (
    <div className="max-w-6xl mx-auto p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-white mb-2">Lists</h1>
        <p className="text-gray-400">Organize tasks by custom lists</p>
      </div>

      {error && (
        <div className="bg-red-500 text-white p-4 rounded-md mb-6">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Sidebar - Lists */}
        <div className="lg:col-span-1">
          <div className="bg-gray-800 rounded-lg p-6">
            <h2 className="text-xl font-semibold text-white mb-4">Your Lists</h2>

            {/* Create New List */}
            <form onSubmit={createList} className="mb-6">
              <div className="flex gap-2">
                <input
                  type="text"
                  placeholder="New list name"
                  value={newListName}
                  onChange={(e) => setNewListName(e.target.value)}
                  className="flex-1 px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
                />
                <button
                  type="submit"
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-all duration-200 transform hover:scale-105"
                >
                  +
                </button>
              </div>
            </form>

            {/* Lists */}
            {loading ? (
              <div className="text-gray-400">Loading lists...</div>
            ) : (
              <div className="space-y-2">
                {lists.map((list) => (
                  <div
                    key={list.id}
                    className={`p-3 rounded-md cursor-pointer transition-all duration-200 hover:bg-gray-700 ${
                      selectedList === list.id ? 'bg-blue-600 text-white' : 'text-gray-300 hover:text-white'
                    }`}
                    onClick={() => setSelectedList(list.id)}
                  >
                    <div className="flex items-center justify-between">
                      <span className="font-medium">{list.name}</span>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          deleteList(list.id);
                        }}
                        className="text-red-400 hover:text-red-300 transition-colors duration-200"
                      >
                        <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                        </svg>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Main Content - Tasks */}
        <div className="lg:col-span-3">
          {selectedList ? (
            <div className="bg-gray-800 rounded-lg p-6">
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-white mb-2">
                  {selectedListData?.name}
                </h2>

                {/* Add Task Form */}
                <form onSubmit={addTaskToList} className="flex gap-4">
                  <input
                    type="text"
                    placeholder="Add a new task..."
                    value={newTask}
                    onChange={(e) => setNewTask(e.target.value)}
                    className="flex-1 px-4 py-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
                  />
                  <button
                    type="submit"
                    className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-md font-medium transition-all duration-200 transform hover:scale-105"
                  >
                    Add Task
                  </button>
                </form>
              </div>

              {/* Tasks List */}
              {tasks.length === 0 ? (
                <div className="text-center text-gray-400 py-12">
                  <p className="text-xl">No tasks in this list!</p>
                  <p className="text-sm mt-2">Add a task above to get started.</p>
                </div>
              ) : (
                <div className="space-y-3">
                  {tasks.map((task) => (
                    <div
                      key={task.id}
                      className={`bg-gray-700 rounded-lg p-4 border-l-4 transition-all duration-200 hover:bg-gray-650 ${
                        task.completed ? 'border-green-500 opacity-75' : 
                        task.priority === 'high' ? 'border-red-500' :
                        task.priority === 'medium' ? 'border-orange-500' : 'border-blue-500'
                      }`}
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-3 flex-1">
                          <button
                            onClick={() => toggleComplete(task.id, task.completed)}
                            className={`w-6 h-6 rounded-full border-2 transition-all duration-200 hover:scale-110 ${
                              task.completed
                                ? 'bg-green-500 border-green-500'
                                : 'border-gray-400 hover:border-green-500'
                            }`}
                          >
                            {task.completed && (
                              <svg className="w-4 h-4 text-white mx-auto" fill="currentColor" viewBox="0 0 20 20">
                                <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                              </svg>
                            )}
                          </button>

                          <div className="flex-1">
                            <h3 className={`text-lg font-medium transition-all duration-200 ${
                              task.completed ? 'text-gray-500 line-through' : 'text-white'
                            }`}>
                              {task.title}
                            </h3>
                            {task.description && (
                              <p className={`text-sm mt-1 ${
                                task.completed ? 'text-gray-600' : 'text-gray-400'
                              }`}>
                                {task.description}
                              </p>
                            )}
                          </div>
                        </div>

                        <div className="flex items-center space-x-3">
                          <span className={`text-sm font-medium ${getPriorityColor(task.priority)}`}>
                            {getPriorityText(task.priority)}
                          </span>

                          <button
                            onClick={() => deleteTask(task.id)}
                            className="text-red-400 hover:text-red-300 transition-colors duration-200 p-1 hover:bg-red-900 rounded"
                          >
                            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                              <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                            </svg>
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          ) : (
            <div className="bg-gray-800 rounded-lg p-6 text-center">
              <p className="text-gray-400">Select a list to view its tasks</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ListsView;
